import { USER_INFORMATION } from "../actions";

const INITIAL_STATE = {
  username: {
    name: '',
  }
};
// isso será usado no projeto

const formReducer = (state = INITIAL_STATE, action) => {
 switch(action.type) {
  case USER_INFORMATION:
    return {
      ...state,
    }
  default:
    return state;
 }
}

export default formReducer;
