import React, { Component } from 'react';
import { connect } from 'react-redux'; // usado para os componentes que lerão o estado

class Form extends Component {
  state = {
    name: '',
    favoriteColor: '',
    favoriteFruit: '',
  }

  handleChange = ({ target }) => {
    const { name, value } = target;

    this.setState({
      [name]: value,
    })
  }

  render() {

    return (
      <div>
        <label htmlFor="name-input">
          Name:
          <input
            type="text"
            name="name"
            id="name-input"
            // value={ name }
          />
        </label>

        <button
          type='button'
          onClick={ () => console.log("name") }
        >
          Salva o nome
        </button>

        <br />
        <br />

        <label htmlFor="favorite-color-input">
          Cor favorita:
          <input
            type="text"
            name="favoriteColor"
            id="favorite-color-input"
            // value={ favoriteColor }
          />
        </label>

        <label htmlFor="favorite-fruit-input">
          Fruta favorita:
          <input
            type="text"
            name="favoriteFruit"
            id="favorite-fruit-input"
            // value={ favoriteFruit }
          />
        </label>

        <button
          type='button'
          onClick={ () => console.log("favoriteColor, favoriteFruit") }
        >
          Salva os favoritos
        </button>

      </div>
    )
  }
}

export default connect()(Form)
