import { REQUEST_ISS_LOCATION } from "../actions";

const INITIAL_STATE = {
  latitude: 0,
  longitude: 0,
  isFetching: true,
  error: '',
}

const issLocationReducer = (state = INITIAL_STATE, action) => {
  switch(action.type) {
    case REQUEST_ISS_LOCATION: return {
      ...state,
      isFetching: true,
    }
    case REQUEST_ISS_LOCATION: return {
      ...state,
      isFetching: true,
    }
    default: return state;
  }
}

export default issLocationReducer;
